import React, {useEffect, useState} from 'react';
import axios from "axios";
import './Convert.css';
import { debug } from 'console';
import { StringMappingType } from 'typescript';

type CurrencyDictionary = {
    [key: string]: number
}
var base = "usd"

export default function Convert() {
    const currency_url = "https://cdn.jsdelivr.net/gh/fawazahmed0/currency-api@1/latest/currencies/" + base + ".json";
//const Convert = () => {

    const [currency, setCurrency] = useState<CurrencyDictionary>({})
    const [toCurrency, setToCurrency] = useState("")
    const [fromCurrency, setFromCurrency] = useState("")
    useEffect(() => {
        axios.get(currency_url).then(({data}) => {
            setCurrency(data[base]);
            setFromCurrency(base)
        }).catch((error) => {
            alert(error);
        });
    },[]);

    //var fromCurrency = base
    //var toCurrency = Object.keys(currency)[0]
    //setToo(Object.keys(currency)[0])
    //var num = currency[toCurrency]
    //console.log(num)

    return (
    <div className="conv">
        <h2>Currencies</h2>
        <div className="currencySelection">
            <div className="inputField">
                <input type="number" className="input"/>
                <input type="number" className="input"/>
            </div>
            <div className="currencyList">
                <select value={fromCurrency}>
                    {Object.keys(currency).map((v => (
                        <option key={v} value={v}>{v}</option>
                    )))}
                </select>
                <select valuealue={toCurrency} onChange={e => setToCurrency(e.target.value)}>
                    {Object.keys(currency).map((v => (
                        <option key={v} value={v}>{v}</option>
                    )))}
                </select>
            </div>
        </div>
        
    </div>
    )
}
