import React, {useEffect, useState} from 'react';
import axios from "axios";
import './Convert.css';

type CurrencyDictionary = {
    [key: string]: number
}
    
var base = "usd"
export default function Convert() {
    const currency_url = "https://cdn.jsdelivr.net/gh/fawazahmed0/currency-api@1/latest/currencies/" + base + "";
//const Convert = () => {

    const [currency, setCurrency] = useState<CurrencyDictionary>({})

    useEffect(() => {
        axios.get(currency_url).then(({data}) => {
            setCurrency(data.eur);
        }).catch((error) => {
            alert(error);
        });
    },[]);

    return (
    <div className="conv">
        <h2>Currencies</h2>
        <div className="select">
            <div className="input">
                <input type="number" className="input" />
                <input type="number" className="input" />
            </div>
            <div className="selection">
                <select>
                    {Object.keys(currency).map(v => (
                        <option key={v} value={v}>{v}</option>
                    ))}
                </select>
                <select>
                    {Object.keys(currency).map(v => (
                        <option key={v} value={v}>{v}</option>
                    ))}
                </select>
            </div>
        </div>
    </div>
    )
}
