import React, {useEffect, useState} from 'react';
import axios from "axios";
import './Convert.css';

type CurrencyDictionary = {
    [key: string]: number
}
    

export default function Convert() {
    const currency_url = "https://cdn.jsdelivr.net/gh/fawazahmed0/currency-api@1/latest/currencies/eur.json";
//const Convert = () => {

    const [currency, setCurrency] = useState<CurrencyDictionary>({})

    useEffect(() => {
        axios.get(currency_url).then(({data}) => {
            setCurrency(data.eur);
        }).catch((error) => {
            alert(error);
        });
    },[]);

    return (
    <div className="conv">
        <h2>Currencies</h2>
        {/* <table>
            <thead>
                <tr>
                    <th>Key</th>
                    <th>Code</th>
                </tr>
            </thead>
            <tbody>
                {Object.keys(currency).map(v => (
                    <tr key={v}>
                        <td>{v}</td>
                        <td>{currency[v]}</td>
                    </tr>
                ))}
            </tbody>
        </table> */}
        <div className="select">
            <input type="number" className="input" />
            <select>
                {Object.keys(currency).map(v => (
                    <option value={v}>{v}</option>
                ))}
            </select>
        </div>
    </div>
    )
}
